import cors from 'cors';
import express from 'express';
import 'express-async-errors';
import swaggerUi from 'swagger-ui-express';
import errorMiddleware from './middlewares/error.middleware';
import linkRoute from './routes/link.route';
import loginRoute from './routes/login.route';
import userRoute from './routes/user.route';
import swaggerFile from './swaggerFile.json';

export default class App {
  public app: express.Express;

  constructor() {
    this.app = express();
    this.app.use(express.json());
    this.app.use(cors());
    this.app.use(loginRoute);

    this.app.use('/doc', swaggerUi.serve, swaggerUi.setup(swaggerFile));
    this.app.use(userRoute);
    this.app.use(linkRoute);
    this.app.use(errorMiddleware);
  }

  public start(PORT: string | number): void {
    this.app.listen(PORT, () => console.log(`Running on port ${PORT}`));
  }
}

// exportação para os testes
export const { app } = new App();

import { ErrorRequestHandler } from 'express';
import { errorCatalog, ErrorTypes } from '../errors/catalog';

const errorMiddleware: ErrorRequestHandler = (err: Error, _req, res, _next) => {
  if (err.name === 'ValidationError') {
    return res.status(400).json({ error: err.message });
  }

  const messageAsErrorType = err.message as keyof typeof ErrorTypes;
  const mappedError = errorCatalog[messageAsErrorType];

  if (mappedError) {
    const { httpStatus, message } = mappedError;
    return res.status(httpStatus).json({ error: message });
  }

  return res.status(500).json({ error: err.message });
};

export default errorMiddleware;

export enum ErrorTypes {
  EntityNotFound = 'EntityNotFound',
  InvalidId = 'InvalidId',
  TokenNotFound = 'TokenNotFound',
  UserAlreadyExists = 'UserAlreadyExists',
  EntityAlreadyExists = 'EntityAlreadyExists',
}

type ErrorResponseObject = {
  message: string;
  httpStatus: number;
};

export type ErrorCatalog = {
  [key in ErrorTypes]: ErrorResponseObject
};

export const errorCatalog: ErrorCatalog = {
  EntityNotFound: {
    message: 'Object not found',
    httpStatus: 404,
  },
  InvalidId: {
    message: 'ID not found',
    httpStatus: 400,
  },
  TokenNotFound: {
    message: 'Token not found',
    httpStatus: 400,
  },
  EntityAlreadyExists: {
    message: 'Label already exists',
    httpStatus: 400,
  },
  UserAlreadyExists: {
    message: 'User already exists',
    httpStatus: 400,
  },
};

