import { Request, Response } from 'express';
import ILink from '../interfaces/ILink';
import IService from '../interfaces/IService';
import JwtService from '../services/JwtService';
import AbstractController from './abstractController';

export default class LinkController extends AbstractController<ILink> {
  public constructor(service: IService<ILink>) {
    super(service);
  }

  public async read(req: Request, res: Response) {
    const { authorization } = req.headers;

    const data = JwtService.validateToken(authorization as string);
    const obj = await this._service.read(data.id);

    return res.status(200).json(obj);
  }
}

import 'dotenv/config';
import * as jwt from 'jsonwebtoken';

export default class JwtService {
  static sign(payload: { email: string, id: number }): string {
    const secret = process.env.JWT_SECRET as string;
    return jwt.sign(payload, secret);
  }

  static validateToken(token: string) {
    try {
      const secret = process.env.JWT_SECRET as string;
      const data = jwt.verify(token, secret);
      return data as jwt.JwtPayload;
    } catch (err) {
      throw new Error('TokenNotFound');
    }
  }
}

import { Request, Response } from 'express';
import IController from '../interfaces/IController';
import IService from '../interfaces/IService';

export default abstract class AbstractController<T> implements IController {
  protected _service: IService<T>;

  constructor(service: IService<T>) {
    this._service = service;
  }

  public async create(req: Request, res: Response): Promise<Response> {
    const obj = await this._service.create(req.body);
    return res.status(201).json(obj);
  }

  public async read(req: Request, res: Response): Promise<Response> {
    const objs = await this._service.read();
    return res.status(200).json(objs);
  }

  public async readOne(req: Request, res: Response): Promise<Response> {
    const obj = await this._service.readOne(req.params.id);
    return res.status(200).json(obj);
  }

  public async update(req: Request, res: Response): Promise<Response> {
    const objUpdated = await this._service.update(req.params.id, req.body);
    return res.status(200).json(objUpdated);
  }

  public async delete(req: Request, res: Response): Promise<Response> {
    await this._service.delete(req.params.id);
    return res.status(204).end();
  }
}

import { NextFunction, Request, Response } from 'express';
import JwtService from '../services/JwtService';
import LoginService from '../services/LoginService';
import validationLogin from '../utils/validateLogin';

export default class LoginController {
  jwtService = JwtService;
  constructor(private service: LoginService) { }
  public async authenticator(req: Request, res: Response) {
    validationLogin(req.body);
    const token = await this.service.authenticator(req.body);
    res.status(200).json({ token });
  }

  public async validationAuthenticator(req: Request, res: Response, next: NextFunction) {
    const { authorization } = req.headers;
    if (!authorization) throw new Error('TokenNotFound');

    await this.jwtService.validateToken(authorization);
    next();
  }
}

import Joi from 'joi';
import ILogin from '../interfaces/ILogin';

const validationLogin = (obj: ILogin) => {
  const schema = Joi.object({
    email: Joi.string().email().required(),
    password: Joi.string().required(),
  });

  const { error, value } = schema.validate(obj);
  if (error) throw error;
  return value;
};

export default validationLogin;

import Link from '../database/models/Link';
import ILink from '../interfaces/ILink';
import IService from '../interfaces/IService';
import { validationBodyLink } from '../utils/validateBody';
import validationIdParams from '../utils/validateIdParams';
import UserService from './UserService';

export default class LinkService implements IService<ILink> {
  private db = Link;
  private userService = new UserService();

  public async create(obj: ILink): Promise<ILink> {
    const objValid = validationBodyLink(obj);
    await this.userService.readOne(objValid.userId);
    const [link, boolean] = await this.db
      .findOrCreate({ where: { label: obj.label }, defaults: { ...objValid } });
    if (!boolean) {
      throw new Error('EntityAlreadyExists');
    }
    return link;
  }

  public async update(id: string, obj: ILink): Promise<ILink> {
    const numberId = validationIdParams(id);
    const objValid = validationBodyLink(obj);
    await this.db.update({ ...objValid }, { where: { id: numberId } });
    return { id: numberId, ...obj };
  }

  public async read(id: number): Promise<ILink[]> {
    const links = await this.db.findAll({ where: { userId: id } });
    return links;
  }

  public async readOne(id: string): Promise<ILink> {
    const numberId = validationIdParams(id);
    const link = await this.db.findByPk(numberId);

    if (!link) throw new Error('EntityNotFound');
    return link;
  }

  public async delete(id: string): Promise<void> {
    const numberId = validationIdParams(id);
    await this.db.destroy({ where: { id: numberId } });
  }
}

import { INTEGER, Model, STRING } from 'sequelize';
import db from '.';
import User from './User';

class Link extends Model {
  id!: number;
  url!: string;
  label!: string;
  userId!: number;
}

Link.init({
  id: {
    type: INTEGER,
    allowNull: false,
    primaryKey: true,
    autoIncrement: true,
  },
  url: {
    type: STRING,
    allowNull: false,
  },
  label: {
    type: STRING,
    allowNull: false,
  },
  userId: {
    type: INTEGER,
    allowNull: false,
  },
}, {
  sequelize: db,
  modelName: 'links',
  underscored: true,
  timestamps: false,
});

Link.belongsTo(User, {
  foreignKey: 'userId',
  as: 'user',
});

User.hasMany(Link, {
  foreignKey: 'userId',
  as: 'links',
});

export default Link;

